import "../Style/Recorder.css";
import { useState, useEffect, useRef } from "react";
import { IonIcon } from "@ionic/react";
import * as Icon from "ionicons/icons";

export default function AudioRecorder(props) {
    const [playIcon, setPlayIcon] = useState(Icon.pause);
    const [time, setTime] = useState("0:00");
    function formatTime(time){
        let seconds = time % 60;
        let minutes = Math.floor(time / 60);
        if(seconds < 10){
            return `${minutes}:0${seconds}`;
        }
        else{
            return `${minutes}:${seconds}`;
        }
    }
    const progressRef = useRef(null);
    useEffect(() => {
        if(props.time !== 0 && props.time !== 100){
            progressRef.current.style.width = `${props.time}%`;
        }
        else if(props.time === 100){
            progressRef.current.style.width = `0%`;
        }
        setTime(formatTime(props.time));
    },[props.time])
    return (
        <div className="recorderContainer" style={{ display: props.visibility }}>
            <div className="maxDuration"><div ref={progressRef} className="currentDuration"></div></div>
            <div className = "recordingTime"></div>
            <IonIcon icon={playIcon} className="pauseIcon"></IonIcon>
            <IonIcon icon={Icon.refresh} className="restartIcon"></IonIcon>
        </div>
    )
}
